import React, { Component } from 'react'
import axios from 'axios';

export default class TodoApp extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos: ['Take out the trash', 'rdv Dentist'],
            done: [], 
            input: ''
        }
    }

    add = (e) => {
        if (e.key === 'Enter') {
            this.state.todos.unshift(this.state.input)
            this.setState({todos: this.state.todos})
            e.target.value = ''
        } 
    }

    onChange = (input) => {
        this.setState({input: input})
    }

    markDone = (todo) => {
        let list = this.state.todos.filter(t => {
            return t != todo
        })
        this.state.done.push(todo)
        this.setState({done: this.state.done, todos: list})
    }

    markUndone = (todo) => {
        let list = this.state.done.filter(t => {
            return t != todo
        })
        this.state.todos.push(todo)
        this.setState({done: list, todos: this.state.todos})
    }

    delete = (done) => {
        let list = this.state.done.filter(t => {
            return t != done
        })
        this.setState({done: list})
    }

    state = {
        id: '',
    }

    handleChange = event => {
        this.setState({ id: event.target.value });
    }

    handleSubmit = event => {
        event.preventDefault();

        axios.post(`http://localhost:4000/todos/create`)
            .then(res => {
                console.log(res);
                console.log(res.data);
            })
    }

    render() {
        return (
                    <form>
                        <form onSubmit={this.handleSubmit}>
                            <label>
                                Person ID:
                                <input type="text" name="id" onChange={this.handleChange} />
                            </label>
                            <button type="submit">Delete</button>
                        </form>
                    </form>
        )
    }
}
